<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Spinning Wheel</title>
    <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet">
    <link rel="stylesheet" href="/dist/css/spinner.css">
</head>
<style>
    :where(.ui-wheel-of-fortune) {
        all: unset;
        aspect-ratio: 1 / 1;
        container-type: inline-size;
        direction: ltr;
        display: grid;
        position: relative;
        width: 500px;
        margin: auto !important;
        border: 15px solid rgb(28, 27, 32);
        border-radius: 50%;
        box-shadow: 0px 0px 10px 1px rgb(64, 61, 61);

        &::after {
            aspect-ratio: 1/cos(30deg);
            background-color: rgb(255, 255, 255);
            clip-path: polygon(50% 100%, 100% 0, 0 0);
            content: "";
            height: 5cqi;
            position: absolute;
            place-self: start center;
            scale: 2.5;
            top: -20px;
        }

        &>* {
            position: absolute;
        }

        button {
            aspect-ratio: 1 / 1;
            background: hsla(0, 0%, 3%);
            border: 0;
            border-radius: 50%;
            cursor: pointer;
            font-size: 5cqi;
            place-self: center;
            width: 20cqi;
            color: aliceblue;
        }

        ul {
            all: unset;
            clip-path: inset(0 0 0 0 round 50%);
            display: grid;
            inset: 0;
            place-content: center start;
            transition: 1s;

            li {
                align-content: center;
                aspect-ratio: 1 / calc(2 * tan(180deg / var(--_items)));
                background: hsl(calc(360deg / var(--_items) * calc(var(--_idx))), 100%, 75%);
                clip-path: polygon(0% 0%, 100% 50%, 0% 100%);
                display: grid;
                font-size: 5cqi;
                grid-area: 1 / -1;
                padding-left: 2.5ch;
                rotate: calc(360deg / var(--_items) * calc(var(--_idx) - 1));
                transform-origin: center right;
                user-select: none;
                width: 50cqi;

                /* &:nth-of-type(1) {
                    --_idx: 1;
                }

                &:nth-of-type(2) {
                    --_idx: 2;
                }

                &:nth-of-type(3) {
                    --_idx: 3;
                }

                &:nth-of-type(4) {
                    --_idx: 4;
                }

                &:nth-of-type(5) {
                    --_idx: 5;
                }

                &:nth-of-type(6) {
                    --_idx: 6;
                }

                &:nth-of-type(7) {
                    --_idx: 7;
                }

                &:nth-of-type(8) {
                    --_idx: 8;
                }

                &:nth-of-type(9) {
                    --_idx: 9;
                }

                &:nth-of-type(10) {
                    --_idx: 10;
                }

                &:nth-of-type(11) {
                    --_idx: 11;
                }

                &:nth-of-type(12) {
                    
                } */
            }
        }
    }

    /* for demo */
    * {
        box-sizing: border-box;
    }

    body {
        font-family: system-ui, sans-serif;
        background-color: rgb(28, 27, 32);
    }

    #edit_button {
        padding: 8px 15px;
        font-size: 20px;
        margin: 50px;
        position: absolute;
        bottom: 0;
        right: 0;
        background-color: rgb(70, 159, 243);
        color: aliceblue;
        border: none;
        outline: none;
        border-radius: 5px;
        cursor: pointer;
    }

    #edit_button:hover {
        background-color: rgb(23, 95, 162);
        color: rgb(255, 255, 255);
    }

    #main-popup {
        width: 100%;
        position: absolute;
        height: 98vh;
        bottom: 0;
        transform: 2s;
    }

    #main-popup .card-body {
        height: 60vh;
        overflow: auto;
    }
</style>

<body>
    <h3 style="text-align: center; color: aliceblue; margin-bottom: 45px; font-size: 50px;" id="winner-box">&nbsp;</h3>
    <fieldset class="ui-wheel-of-fortune" style="--_items: 4;">
        <ul>
            <li style="--_idx: 1;">1</li>
            <li style="--_idx: 2;">2</li>
            <li style="--_idx: 3;">3</li>
            <li style="--_idx: 4;">4</li>
        </ul>
        <button type="button">START</button>
    </fieldset>
    <audio id="myAudio">
        <source
            src="data:audio/mpeg;base64,"
            type="audio/mpeg">
        Your browser does not support the audio element.
    </audio>

    <button id="edit_button"> &#128393; </button>

    <div id="main-popup">
        <div class="container mt-5">
            <div class="card">
                <div class="card-header d-flex justify-content-between align-items-center">
                    <h6 class="mb-0 close"> &#x2716; </h5>
                        <input type="text" class="btn btn-link text-decoration-none input-component px-5 py-1"
                            value="Lucky Spinner Wheel">
                        <button class="btn btn-link text-decoration-none"></button>
                </div>
                <div class="card-body">
                    <ul class="list-group item-list">
                        <li class="list-group-item d-flex flex-grow-1 align-items-center justify-content-between"
                            style="gap: 20px;">
                            <div class="d-flex flex-grow-1 align-items-center">
                                <div class="circle bg-danger me-3"></div>
                                <input type="text" class="input-component btn btn-link py-1 pl-1 flex-grow-1 item-name"
                                    style="text-align: left" />
                            </div>
                            <div class="d-flex align-items-center ">
                                <input type="number"
                                    class="form-control number-section form-control-sm w-25 me-3 text-center item-weight" value="1">
                                <button class="btn btn-danger btn-sm circle remove-item" style="padding: 0 0 4px 0;"
                                    title="Remove item">&#8722</button>
                            </div>
                        </li>
                        <li class="list-group-item d-flex flex-grow-1 align-items-center justify-content-between"
                            style="gap: 20px;">
                            <div class="d-flex flex-grow-1 align-items-center">
                                <div class="circle bg-danger me-3"></div>
                                <input type="text" class="input-component btn btn-link py-1 pl-1 flex-grow-1 item-name"
                                    style="text-align: left" />
                            </div>
                            <div class="d-flex align-items-center ">
                                <input type="number"
                                    class="form-control number-section form-control-sm w-25 me-3 text-center item-weight" value="1">
                                <button class="btn btn-danger btn-sm circle remove-item" style="padding: 0 0 4px 0;"
                                    title="Remove item">&#8722</button>
                            </div>
                        </li>
                        <li class="list-group-item d-flex flex-grow-1 align-items-center justify-content-between"
                            style="gap: 20px;">
                            <div class="d-flex flex-grow-1 align-items-center">
                                <div class="circle bg-danger me-3"></div>
                                <input type="text" class="input-component btn btn-link py-1 pl-1 flex-grow-1 item-name"
                                    style="text-align: left" />
                            </div>
                            <div class="d-flex align-items-center ">
                                <input type="number"
                                    class="form-control number-section form-control-sm w-25 me-3 text-center item-weight" value="1">
                                <button class="btn btn-danger btn-sm circle remove-item" style="padding: 0 0 4px 0;"
                                    title="Remove item">&#8722</button>
                            </div>
                        </li>
                        <li class="list-group-item d-flex flex-grow-1 align-items-center justify-content-between"
                            style="gap: 20px;">
                            <div class="d-flex flex-grow-1 align-items-center">
                                <div class="circle bg-danger me-3"></div>
                                <input type="text" class="input-component btn btn-link py-1 pl-1 flex-grow-1 item-name"
                                    style="text-align: left" />
                            </div>
                            <div class="d-flex align-items-center ">
                                <input type="number"
                                    class="form-control number-section form-control-sm w-25 me-3 text-center item-weight" value="1">
                                <button class="btn btn-danger btn-sm circle remove-item" style="padding: 0 0 4px 0;"
                                    title="Remove item">&#8722</button>
                            </div>
                        </li>
                    </ul>
                </div>
                <div class="card-footer d-flex justify-content-between flex-column align-items-center"
                    style="gap: 10px">
                    <button class="btn btn-primary " style="padding: 10px 30px;" id="add_new_item">Add new item +
                    </button>
                    <button class="btn btn-success" style="padding: 10px 100px;" id="save_item_button">Done</button>
                </div>
            </div>
        </div>
    </div>

    <script>

        let wheelItems = [];
        function wheelOfFortune(selector) {
            const node = document.querySelector(selector);
            const sound = document.getElementById('myAudio')
            if (!node) return;

            const spin = node.querySelector('button');
            const wheel = node.querySelector('ul');
            let animation;
            let previousEndDegree = 0;
            let spinDuration = 5000;

            spin.addEventListener('click', () => {
                let total = wheelItems.length;
                setTimeout(() => {
                    sound.play();
                }, 500);

                if (animation) {
                    animation.cancel(); // Reset the animation if it already exists
                }
                const randomAdditionalDegrees = Math.random() * 360 + 1800;
                const newEndDegree = previousEndDegree + randomAdditionalDegrees;
                const spinDegError = 90 + (360 / total) / 2;
                resultIndex = Math.floor(total - ((newEndDegree - spinDegError) % 360) / (360 / total));
                animation = wheel.animate([
                    { transform: `rotate(${previousEndDegree}deg)` },
                    { transform: `rotate(${newEndDegree}deg)` }
                ], {
                    duration: spinDuration,
                    direction: 'normal',
                    easing: 'cubic-bezier(0.440, -0.205, 0.000, 1.130)',
                    fill: 'forwards',
                    iterations: 1
                });
                setTimeout(function () {
                    show_winner(resultIndex);
                }, spinDuration);
                previousEndDegree = newEndDegree;
            });

            function show_winner(id) {
                document.querySelector('#winner-box').innerHTML = wheelItems[id].name;
            }
        }

        // Usage
        wheelOfFortune('.ui-wheel-of-fortune');

        document.querySelector('#add_new_item').addEventListener('click', function () {
            let inputContainer = document.querySelector('.item-list')
            const newRow = document.createElement('li');
            newRow.classList.add('list-group-item', 'd-flex', 'flex-grow-1', 'align-items-center', 'justify-content-between');
            newRow.style.gap = '20px';

            const randomColor = `hsl(${Math.random() * 360}, 70%, 60%)`;

            newRow.innerHTML = `
                <div class="d-flex flex-grow-1 align-items-center">
                    <div class="circle me-3" style="background-color: ${randomColor};"></div>
                    <input type="text" class="input-component btn btn-link py-1 pl-1 flex-grow-1 item-name" style="text-align: left"/>
                </div>
                <div class="d-flex align-items-center ">
                    <input type="number"
                        class="form-control number-section form-control-sm w-25 me-3 text-center item-weight" value="1">
                    <button class="btn btn-danger btn-sm circle remove-item" style="padding: 0 0 4px 0;" title="Remove item">&#8722</button>
                </div>
                `;
            inputContainer.appendChild(newRow);
            newRow.querySelector('.remove-item').addEventListener('click', function () {
                newRow.remove();
            })
        });

        document.querySelectorAll('.remove-item').forEach(function (element) {
            element.addEventListener('click', () => {
                element.parentElement.parentElement.remove();
            });
        })

        document.querySelector('#save_item_button').onclick = function () {
            let inputContainer = document.querySelector('.item-list');
            let i = 0;
            console.log(inputContainer)
            inputContainer.querySelectorAll('.list-group-item').forEach(function (row, index) {
                console.log(row);
                let name = row.querySelector('.item-name').value;
                let number = row.querySelector('.item-weight').value;
                console.log(name, number);
                if (name.trim() != '' && number.trim() != '') {
                    wheelItems[i++] = {
                        'name': name,
                        'number': number,
                    };
                }
            });
            create_sppinner();
            hidePopUp();
        };

        function showPopUp(){
            document.querySelector('#main-popup').style.display = "block";
        }
        function hidePopUp(){
            document.querySelector('#main-popup').style.display = "none";
        }

        document.querySelector('#edit_button').addEventListener('click', function(){
            showPopUp();
        });

        function create_sppinner() {
            let total = wheelItems.length;
            let container = document.querySelector('.ui-wheel-of-fortune ul');
            document.querySelector('.ui-wheel-of-fortune').setAttribute('style', `--_items: ${total};`);
            container.innerHTML = "";
            // <li style="--_idx: 1;">$1000</li>
            let idx = 1;
            wheelItems.forEach(element => {
                let li = document.createElement('li');
                li.setAttribute('style', `--_idx: ${idx++};`);
                li.innerHTML = element.name
                container.appendChild(li);
            });
        }
    </script>
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"></script>
</body>

</html>